import axiosInstance from "./axiosConfig"





export const loanCacalculatorlculator = (amount, numberOfMonths) => {
  const annualInterestRate = 0.5; // Annual interest rate in percentage
  const monthlyInterestRate = annualInterestRate / 12 / 100; // Convert annual rate to monthly and to a decimal
  
  const totalInterest = amount * monthlyInterestRate * numberOfMonths;
  const totalAmountToBePaid = amount + totalInterest;
  const monthlyReturn = totalAmountToBePaid / numberOfMonths;

  return {
    interest: totalInterest.toFixed(2),
    totalAmountToBePaid: totalAmountToBePaid.toFixed(2),
    monthlyReturn: monthlyReturn.toFixed(2),
  };
}; 

export const getAllConsultation  = async ()=> {
  const response = await axiosInstance.get("/getalluserconsultation");

  return response.data
    
}

