            <Route path="/calculator" element={<Calculator />} /> {/* Example route */}
            <Route path="/fundwallet" element={<FundWallet />} /> {/* Example route */}

          </Routes>
        </div>
      </div>
      <Footer />
    </div>
  );
}

export default App;
