import React, { useState, useEffect } from 'react';
import { Sidebar, Menu, MenuItem, SubMenu } from 'react-pro-sidebar';
import { Link, useNavigate } from 'react-router-dom';
import TransactionHistory from './transactionHistory';
import LoanHistory from './loanHistory';
import { getDashboardDetails } from '../services/userServices';


const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [savings, setSavings] = useState([]);
  const [loans, setLoans] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    getDashboardDetails()
      .then((response) => {
        if (response.status === 200) {
          setUser(response.data.user);
          setSavings(response.data.savings);
          setLoans(response.data.loans);
          console.log(response.data.lo, "Savings data");
        } else {
          // Handle unauthorized access or redirect to login
          navigate('/login');
        }
      })
      .catch((error) => console.error(error));
  }, [navigate]);

  return (
    <div className="flex h-screen">
      <Sidebar className="lg:w-1/5 lg:block">
        <Menu>
          <MenuItem>Hello, {user && user.name}</MenuItem>
          <SubMenu label="User Profile">
            <MenuItem>
              <p>UserInfo</p>
            </MenuItem>
          </SubMenu>
          <MenuItem>Wallet Balance: {savings.balance}</MenuItem>
          <MenuItem>Interest Earned: {savings.interest}</MenuItem>
          <MenuItem>Settings</MenuItem>
          <SubMenu label="Loan">
            <MenuItem>Request</MenuItem>
            <MenuItem>Cancel</MenuItem>
          </SubMenu>
        </Menu>
      </Sidebar>
      <div className="flex-1 p-4">
        <div className="mt-10">
          <TransactionHistory data={savings} />
        </div>
        <div className="mt-20">
          <LoanHistory data={loans} />
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
