import React from 'react';
import { motion } from 'framer-motion';
import { fadeIn } from 'react-animations';
import styled, { keyframes } from 'styled-components';



const articles = [
  {
    author: 'By Claire Robinson',
    title: 'Receive money in any currency with no fees',
    description:
      'The world is getting smaller and we’re becoming more mobile. So why should you be forced to only receive money in a single …',
    imgPath: '/images/cash.jpeg',
    imgAlt: 'multiple bills with different values and currencies',
  },
  {
    author: 'By Wilson Hutton',
    title: 'Treat yourself without worrying about money',
    description:
      'Our simple budgeting feature allows you to separate out your spending and set realistic limits each month. That means you …',
    imgPath: '/images/image-restaurant.jpg',
    imgAlt: 'someone eating a hamburguer in a restaurant',
  },
  {
    author: 'By Wilson Hutton',
    title: 'Take your Easybank card wherever you go',
    description:
      'We want you to enjoy your travels. This is why we don’t charge any fees on purchases while you’re abroad. We’ll even show you …',
    imgPath: '/images/image-plane.jpg',
    imgAlt: 'A plane in the sky',
  },
  {
    author: 'By Claire Robinson',
    title: 'Our invite-only Beta accounts are now live!',
    description:
      'After a lot of hard work by the whole team, we’re excited to launch our closed beta. It’s easy to request an invite through the site ...',
    imgPath: '/images/image-confetti.jpg',
    imgAlt: 'Falling confetti',
  },
];

const fadeInAnimation = keyframes`${fadeIn}`;

const AnimatedSection = styled.section`
  animation: ${fadeInAnimation} 1s;
`;

const IconSlider = styled.div`
  display: flex;
  overflow: hidden;
  white-space: nowrap;
`;

const Icon = styled.div`
  display: inline-block;
  width: 50px;
  height: 50px;
  background-color: #ccc;
  margin: 0 10px;
`;

const iconVariants = {
  animate: {
    x: [0, -200],
    transition: {
      x: {
        repeat: Infinity,
        repeatType: 'loop',
        duration: 5,
        ease: 'linear'
      }
    }
  }
};

const Blog = () => {
  return (
    <AnimatedSection className="py-14 lg:py-24">
      <div className="container">
        <h2 className="text-center text-3xl lg:text-4xl text-primary-dark-blue mb-5 lg:text-left lg:mb-10">
          Latest Articles
        </h2>
        <div className="grid grid-cols-1 gap-4 lg:gap-4 lg:grid-cols-4">
          
          ))}
        </div>
        <IconSlider>
          <motion.div
            className="flex"
            variants={iconVariants}
            animate="animate"
          >
            <Icon />
            <Icon />
            <Icon />
            <Icon />
            <Icon />
            <Icon />
            {/* Add more icons as needed */}
          </motion.div>
        </IconSlider>
      </div>
    </AnimatedSection>
  );
}




export default Blog
