import React , {useEffect, useState}from 'react';
import { Sidebar, Menu, MenuItem, SubMenu } from 'react-pro-sidebar';
import { Link, useNavigate } from "react-router-dom";
import TransactionHistory from "./transactionHistory"
import LoanHistory from "./loanHistory"
import {getDashboardDetails} from "../services/userServices"


const Dashboard = () => {
  const [user, setUser] = useState()
  const [savings, setSavings] = useState()
  const [loan, setLoan] = useState()
  const navigate = useNavigate()
  useEffect(() => {
     getDashboardDetails()
    .then((response)=>{
      console.log(response)
      if (response.status===200){
        setUser(response.user);
        setUser(response.loan);
        set(response.savings);

      }else{
       // navigate("/login")
      }
    })
      
      
      
  
    //fetchConsultations(); 
  }, []);




  return (
    <div className="flex h-screen">
      <Sidebar className="lg:w-1/5 lg:hidden">
        <Menu>
          <MenuItem>hi {"kelvin"}</MenuItem>
          <SubMenu label="User Profile">
            <MenuItem>
              <p>UserInfo</p>
            </MenuItem>
          </SubMenu>
          <MenuItem> wallet balance </MenuItem>
          <MenuItem>Interest Earned</MenuItem>
          <MenuItem>Settings</MenuItem>
          <SubMenu label="Loan">
            <MenuItem>Request</MenuItem>
            <MenuItem>Cancel </MenuItem>
          </SubMenu>
        
        </Menu>
      </Sidebar>
      <div className="flex-1 p-4 lg:w-full">
        <div className='mt-10'>
        <TransactionHistory data={savings}/>
        </div>
       
        <div className='mt-20'>
        <LoanHistory data={loan}/>
      </div>
        
        
      </div>
    </div>
  );
};

export default Dashboard;
