import React, { useState, useEffect } from 'react';
import { Sidebar, Menu, MenuItem, SubMenu } from 'react-pro-sidebar';
import { Link, useNavigate } from 'react-router-dom';
import TransactionHistory from './transactionHistory';
import LoanHistory from './loanHistory';
import { getDashboardDetails, interestEarned } from '../services/userServices';
import { Bars3Icon, XMarkIcon } from "@heroicons/react/24/outline"; // Ensure you have these icons

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [savings, setSavings] = useState([]);
  const [loans, setLoans] = useState([]);
  const [cashout, setCashOut]= useState()
  const [sidebarOpen, setSidebarOpen] = useState(false); // State for sidebar toggle
  const navigate = useNavigate();
//principal, despositedDate,calculatedDate
  useEffect(() => {
    getDashboardDetails()
      .then((response) => {
        if (response.status === 200) {
          const date = response.data.savings[0].date;
          const interest = response.data.savings[0].interest;
          const balance = response.data.savings[0].balance;
          const despositedDate = date.toISOString().slice(0, 10)
          
          
          
           setCashOut (cash)
          setUser(response.data.user);
         setSavings({ interest, balance});
          setLoans(response.data.loans);
        } else {
          // Handle unauthorized access or redirect to login
          navigate('/login');
        }
      })
      .catch((error) => console.error(error));
  }, [navigate]);

  const handleSidebarToggle = () => {
    setSidebarOpen(!sidebarOpen);
  };

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <div className={`lg:w-1/5 lg:block ${sidebarOpen ? 'block' : 'hidden'} fixed inset-0 z-50 bg-gray-800 lg:bg-transparent lg:relative`}>
        <Sidebar className="h-full lg:h-auto">
          <Menu>
            <MenuItem>Hello, {user && user.name}</MenuItem>
            <SubMenu label="User Profile">
              <MenuItem>
                <p>UserInfo</p>
              </MenuItem>
            </SubMenu>
            <MenuItem>Wallet Balance: {savings.balance}</MenuItem>
            <MenuItem>Interest Earned: {cashout}</MenuItem>
            <MenuItem>Settings</MenuItem>
            <SubMenu label="Loan">
              <MenuItem>Request</MenuItem>
              <MenuItem>Cancel</MenuItem>
            </SubMenu>
          </Menu>
        </Sidebar>
      </div>

      {/* Main content */}
      <div className="flex-1 p-4 lg:ml-64">
        <div className="flex justify-between items-center lg:hidden">
          <button onClick={handleSidebarToggle}>
            {sidebarOpen ? <XMarkIcon className="w-5 text-gray-700" /> : <Bars3Icon className="w-5 text-gray-700" />}
          </button>
          <h1 className="text-xl font-bold">Dashboard</h1>
        </div>

        <div className="mt-10">
          <TransactionHistory data={savings} />
        </div>
        <div className="mt-20">
          <LoanHistory data={loans} />
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
