import { Routes, Route, Link } from 'react-router-dom';
import  React ,{ useState } from 'react';
import { Bars3Icon} from "@heroicons/react/24/outline" 
import './App.css';
import Login from './features/pages/login';
import Navbar from './features/pages/navbar';
import Footer from './features/pages/footer';
import Home from './features/pages/home';
import PageRouter from "./features/pages/pageRouter"

function App() {
  const [open, setOpen] = useState(true);
  const Menus = [
    { title: 'Dashboard', src: 'Chart_fill', path: '/' },
    { title: 'Loan Status', src: 'Chat', path: '/loanhistory' },
    { title: 'Loan Caculator', src: 'User', path: '/calculator', gap: true },
    { title: 'Fund Wallet', src: 'Search', path: '/fundwallet' },
    { title: 'Analytics', src: 'Chart', path: '/analytics' },
    { title: 'Files', src: 'Folder', path: '/files', gap: true },
    { title: 'Setting', src: 'Setting', path: '/setting' },
  ];


  return (
    <div className="flex flex-col h-screen">
      <Navbar />
      <div className="flex flex-1">
        <div
          className={`${
            open ? 'w-20' : 'w-10'
          } bg-dark-purple h-full p-5 pt-8 relative duration-300`}
        >
          <img
            src="./assets/control.png"
            className={`absolute cursor-pointer  -right-3 top-9 w-7 border-dark-purple border-2 rounded-full ${
              !open && 'rotate-180'
            }`}
            onClick={() => setOpen(!open)}
            alt="Toggle Sidebar"
          /><Bars3Icon className='w-5' />
          <div className="flex gap-x-4 items-center">
            <img
              src="./assets/logo.png"
              className={`cursor-pointer duration-500 ${open && 'rotate-[360deg]'}`}
              alt="Logo"
            />
            <h1
              className={`text-white origin-left font-medium text-xl duration-200 ${
                !open && 'scale-0'
              }`}
            >
              Designer
            </h1>
          </div>
          <ul className="pt-6">
            {Menus.map((Menu, index) => (
              <li
                key={index}
                className={`flex rounded-md p-2 cursor-pointer hover:bg-light-white text-gray-300 text-sm items-center gap-x-4 ${
                  Menu.gap ? 'mt-9' : 'mt-2'
                } ${index === 0 && 'bg-light-white'}`}
              >
                <img src={`./assets/${Menu.src}.png`} alt={Menu.title} />
                <span className={`${!open && 'hidden'} origin-left duration-200`}>
                  {Menu.title}
                </span>
              </li>
            ))}
          </ul>
        </div>
        <div className="flex-1 p-7 overflow-auto">
        <PageRouter/>
        </div>
      </div>
      <Footer />
    </div>
  );
}

export default App;
