import React, { useState, useEffect } from 'react';
import { Sidebar, Menu, MenuItem, SubMenu } from 'react-pro-sidebar';
import { Link, useNavigate } from 'react-router-dom';
import TransactionHistory from './transactionHistory';
import LoanHistory from './loanHistory';
import { getDashboardDetails } from '../services/userServices';
import { Bars3Icon, XMarkIcon } from "@heroicons/react/24/outline";

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [savings, setSavings] = useState([]);
  const [loans, setLoans] = useState([]);
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    getDashboardDetails()
      .then((response) => {
        if (response.status === 200) {
          setUser(response.data.user);
          setSavings(response.data.savings);
          setLoans(response.data.loans);
        } else {
          navigate('/login');
        }
      })
      .catch((error) => console.error(error));
  }, [navigate]);

  const handleSidebarToggle = () => {
    setSidebarOpen(!sidebarOpen);
  };

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <div className={`lg:w-64 ${sidebarOpen ? 'block' : 'hidden'} lg:block bg-gray-800 md:w-full md:flex md:flex-col md:justify-center`}>
        <Sidebar className="h-full md:h-screen">
          <Menu>
            <MenuItem className="text-white">Hello, {user && user.name}</MenuItem>
            <SubMenu label="User Profile" className="text-white">
              <MenuItem className="text-white">
                <p>UserInfo</p>
              </MenuItem>
            </SubMenu>
            <MenuItem className="text-white">Wallet Balance: {savings.balance}</MenuItem>
            <MenuItem className="text-white">Interest Earned: {savings.interest}</MenuItem>
            <MenuItem className="text-white">Settings</MenuItem>
            <SubMenu label="Loan" className="text-white">
              <MenuItem className="text-white">Request</MenuItem>
              <MenuItem className="text-white">Cancel</MenuItem>
            </SubMenu>
          </Menu>
        </Sidebar>
      </div>
      {/* Main content */}
      <div className={`flex-1 p-4 ${sidebarOpen ? 'lg:ml-64' : ''} md:p-0 md:flex md:flex-col md:justify-center`}>
        <div className="flex justify-between items-center lg:hidden md:flex md:flex-col md:justify-center">
          <button onClick={handleSidebarToggle}>
            {sidebarOpen ? <XMarkIcon className="w-5 text-gray-700" /> : <Bars3Icon className="w-5 text-gray-700" />}
          </button>
          <h1 className="text-xl font-bold md:text-3xl">Dashboard</h1>
        </div>
        <div className="mt-10 md:mt-0">
          <TransactionHistory data={savings} />
        </div>
        <div className="mt-20 md:mt-0">
          <LoanHistory data={loans} />
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
