import React, { useState } from 'react';
import {Link} from "react-router-dom"
import { NotificationContainer, NotificationManager } from 'react-notifications';
import 'react-notifications/lib/notifications.css';

import { useNavigate } from 'react-router-dom';
import {loanCaculator} from "../services/userServices"
import {passwordValidation, 
        emailValidation,
      } from "../services/validationService"

const LoanCaculators = () => {
  const navigate = useNavigate()
  const [duration, set] = useState('');
  const [amount, setAmount] = useState('');
 

  const handleCaculate = async (event) => {
    event.preventDefault();
    /*if (!passwordValidation()){
      NotificationManager.error("Password Must Be More Than Four Digits","Invalid Password" );
      return
    }
*/ 
   
 
  };

  
  const handleAmount = (e)=>{
    const emailValue = e.target.value
    setEmail(emailValue.toLowerCase())

    
  }
  const handleDuration = (e)=>{
    setPassword(e.target.value)
    
  }
  
  return (
  <>
    <div className="w-full max-w-sm md:mx-[450px] mt-20 p-4 bg-white border border-gray-200 rounded-lg shadow sm:p-6 md:p-8 dark:bg-gray-800 dark:border-gray-700">
    <div className="space-y-6">
        <h5 className="text-xl font-medium text-gray-900 dark:text-white"> Loan Caculator</h5>
        <h5 className="text-xl font-medium text-gray-900 dark:text-white"> </h5>
        <div>
            <input placeholder="Amount"   onChange={handleAmount}  type="amount"className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-600 dark:border-gray-500 dark:placeholder-gray-400 dark:text-white" required />
        </div>
        <div>
            <select type="duration" placeholder="Select Duration" onChange={handleDuration}  className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-600 dark:border-gray-500 dark:placeholder-gray-400 dark:text-white" required >
            <option value={"annual"} > One Year</option>
            <option value={"quartly"} > Six Month</option>

            </select>
        </div>
        
        <button onClick={handleCaculate}  className="w-full text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800"> Caculate</button>
        <div className="text-sm font-medium text-gray-500 dark:text-gray-300">
            Request For Loan? <Link to={"/loanrequest"} className="text-blue-700 hover:underline dark:text-blue-500">Requst</Link>
        </div>
    </div>
    <NotificationContainer />
</div>
    </>
  );
};

export default LoanCaculators;
